div.script:
$(function(){
    var $gallery = $('.cms-gallery-slideshow-gallery6106eb689b92a');

    if ($gallery.children().length) {
        $gallery.bxSlider({
            minSlides: 1,
            maxSlides: 1,
            pager: false,
            infiniteLoop: false,
            hideControlOnEnd: true,
            adaptiveHeight: true,
            responsive: true
        });
    }
});
